const R  = 8.314_459_8*J/(mol*K)    # (48) molar gass constant
const k  = 1.380_648_52e-23*(J/K)   # (79) Boltzmann constant
const Ïƒ  = Ï€^2*k^4/(60*Ä§^3*c^2)     # Stefan-Boltzmann constant

